SELECT ID,IMAGE,TITLE,DESC_M,NAME,PHONE,CREATE_TIME,UPDATE_TIME,BEGIN_TIME,END_TIME,SIGN_TOTAL,STATUS,EXCEL_PATH
FROM  t_meeting WHERE 1=1

	AND STATUS = #{status}


	AND TITLE LIKE concat(concat('%',#{keyword}),'%')

ORDER BY CREATE_TIME DESC
limit #{index},#{pageSize}

SELECT COUNT(ID) FROM  t_meeting WHERE 1=1

	AND STATUS = #{status}


	AND TITLE LIKE concat(concat('%',#{keyword}),'%')
