mapper.select:
SELECT ID,NAME,HEAD_IMAGE,PHONE,SIGN_CODE,COMPANY,PERSON_TYPE,POSITION,JOIN_DINNER,CREATE_TIME,UPDATE_TIME,DESC_M,STATUS,MEETING_ID
FROM  t_meeting_sign
WHERE MEETING_ID=#{id}

	AND STATUS = #{status}


	AND NAME LIKE concat(concat('%',#{keyword}),'%')

ORDER BY CREATE_TIME DESC
limit #{index},#{pageSize}

SELECT COUNT(ID)
FROM  t_meeting_sign
WHERE MEETING_ID=#{id}

	AND STATUS = #{status}


	AND NAME LIKE concat(concat('%',#{keyword}),'%')

SELECT ID,NAME,HEAD_IMAGE,PHONE,SIGN_CODE,COMPANY,PERSON_TYPE,POSITION,JOIN_DINNER,CREATE_TIME,UPDATE_TIME,DESC_M,STATUS,MEETING_ID
FROM  t_meeting_sign
WHERE MEETING_ID=#{meetingId}
ORDER BY CREATE_TIME DESC
limit #{index},#{pageSize}
